import React, { useEffect, useState } from 'react';

const ReportTab = () => {
  const [bookings, setBookings] = useState([]);
  const [filteredBookings, setFilteredBookings] = useState([]);
  const [serviceType, setServiceType] = useState('');
  const [date, setDate] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/api/bookings')
      .then((res) => res.json())
      .then((data) => {
        setBookings(data);
        setFilteredBookings(data);
      })
      .catch(console.error);
  }, []);

  useEffect(() => {
    let filtered = bookings;

    if (serviceType) {
      filtered = filtered.filter((b) =>
        b.business_type.toLowerCase().includes(serviceType.toLowerCase())
      );
    }

    if (date) {
      filtered = filtered.filter((b) => b.date === date);
    }

    setFilteredBookings(filtered);
  }, [serviceType, date, bookings]);

  const countByDate = filteredBookings.reduce((acc, booking) => {
    acc[booking.date] = (acc[booking.date] || 0) + 1;
    return acc;
  }, {});

  const sortedDates = Object.keys(countByDate).sort(
    (a, b) => new Date(b) - new Date(a)
  );

  return (
    <div className="p-4">
      <h2 className="text-xl font-semibold mb-4">ðŸ“ˆ Booking Reports</h2>

      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-4 mb-6">
        <input
          type="text"
          placeholder="Filter by Service Type"
          value={serviceType}
          onChange={(e) => setServiceType(e.target.value)}
          className="border p-2 rounded w-full sm:w-1/2"
        />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="border p-2 rounded w-full sm:w-1/2"
        />
      </div>

      {filteredBookings.length === 0 ? (
        <p>No bookings found for selected filters.</p>
      ) : (
        <table className="w-full mt-4 text-sm border">
          <thead>
            <tr className="bg-gray-100">
              <th className="p-2 border text-left">ðŸ“… Date</th>
              <th className="p-2 border text-left">ðŸ§¾ Total Appointments</th>
            </tr>
          </thead>
          <tbody>
            {sortedDates.map((date) => (
              <tr key={date}>
                <td className="p-2 border">{date}</td>
                <td className="p-2 border">{countByDate[date]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ReportTab;
