import React, { useState } from 'react';

const SlotsTab = () => {
  const [slots, setSlots] = useState([]);
  const [time, setTime] = useState('');

  const addSlot = () => {
    if (!time) return;
    setSlots([...slots, { id: Date.now(), time }]);
    setTime('');
  };

  const deleteSlot = (id) => {
    setSlots(slots.filter(s => s.id !== id));
  };

  return (
    <div>
      <h3 className="text-xl font-semibold mb-4">Manage Time Slots</h3>
      <div className="flex gap-2 mb-4">
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          className="border p-2 rounded"
        />
        <button onClick={addSlot} className="bg-blue-600 text-white px-4 py-2 rounded">Add</button>
      </div>
      <ul>
        {slots.map(slot => (
          <li key={slot.id} className="flex justify-between border-b py-2">
            {slot.time}
            <button onClick={() => deleteSlot(slot.id)} className="text-red-600">Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SlotsTab;
