import React, { useState, useEffect } from 'react';
import BookingForm from '../components/BookingForm';
import TimeSlotSelector from '../components/TimeSlotSelector';
import { fetchAvailableSlots } from '../utils/api';

const businessTypes = [
  { id: 'doctor', name: 'Doctor / Clinic' },
  { id: 'salon', name: 'Salon / Spa' },
  { id: 'coach', name: 'Coach / Trainer' },
  { id: 'consultant', name: 'Consultant' }
];

const steps = ['Service', 'Time', 'Details', 'Payment', 'Done'];

const Booking = () => {
  const [step, setStep] = useState(0);
  const [selectedType, setSelectedType] = useState(null);
  const [formData, setFormData] = useState({});
  const [date, setDate] = useState('');
  const [slots, setSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [submitted, setSubmitted] = useState(false);

  // Load available time slots
  useEffect(() => {
    if (date && selectedType) {
      fetchAvailableSlots(date).then(setSlots).catch(console.error);
    }
  }, [date, selectedType]);

  const handleNext = () => {
    if (step < steps.length - 1) setStep((prev) => prev + 1);
  };

  const handleBack = () => {
    if (step > 0) setStep((prev) => prev - 1);
  };

  const handleSubmit = async () => {
    const payload = {
      ...formData,
      businessType: selectedType?.id,
      date,
      time: selectedSlot?.time || '',
    };

    try {
      const res = await fetch('http://localhost:5000/api/bookings', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const result = await res.json();
      if (res.ok) {
        setSubmitted(true);
        setStep(4); // Go to "Done" step
      } else {
        alert(result.message || 'Failed to book appointment');
      }
    } catch {
      alert('Something went wrong');
    }
  };

  return (
    <div className="p-8 max-w-4xl mx-auto">
      {/* Step Bar */}
      <div className="flex items-center justify-between mb-8">
        {steps.map((label, index) => (
          <div key={index} className="text-center flex-1">
            <div className={`mb-1 text-sm ${index === step ? 'text-blue-600 font-bold' : 'text-gray-500'}`}>
              {index + 1}. {label}
            </div>
            <div className={`h-2 rounded ${index <= step ? 'bg-blue-500' : 'bg-gray-300'}`} />
          </div>
        ))}
      </div>

      {/* Step Content */}
      {step === 0 && (
        <div>
          <h3 className="text-lg font-medium mb-2">Please select a service type:</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            {businessTypes.map((type) => (
              <button
                key={type.id}
                className={`border p-4 rounded-lg ${
                  selectedType?.id === type.id ? 'bg-blue-100 border-blue-500' : 'border-gray-300'
                }`}
                onClick={() => setSelectedType(type)}
              >
                {type.name}
              </button>
            ))}
          </div>
          <div className="mt-6 text-right">
            <button
              disabled={!selectedType}
              className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:bg-gray-400"
              onClick={handleNext}
            >
              Next
            </button>
          </div>
        </div>
      )}

      {step === 1 && (
        <div>
          <label className="block text-sm mb-2">Select Date:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => {
              setDate(e.target.value);
              setSelectedSlot(null);
            }}
            className="border p-2 rounded w-full mb-4"
          />
          {date && slots.length > 0 && (
            <>
              <label className="block text-sm mb-2">Choose a Time Slot:</label>
              <TimeSlotSelector slots={slots} onSelect={setSelectedSlot} />
            </>
          )}
          <div className="mt-6 flex justify-between">
            <button onClick={handleBack} className="text-blue-600">Back</button>
            <button
              onClick={handleNext}
              disabled={!selectedSlot}
              className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:bg-gray-400"
            >
              Next
            </button>
          </div>
        </div>
      )}

      {step === 2 && (
        <div>
          <h3 className="text-lg font-medium mb-4">Enter your details:</h3>
          <BookingForm formData={formData} setFormData={setFormData} />
          <div className="mt-6 flex justify-between">
            <button onClick={handleBack} className="text-blue-600">Back</button>
            <button
              onClick={handleNext}
              disabled={!formData.name || !formData.email}
              className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:bg-gray-400"
            >
              Next
            </button>
          </div>
        </div>
      )}

      {step === 3 && (
        <div>
          <h3 className="text-lg font-medium mb-2">Payment (Optional)</h3>
          <p className="text-gray-600 mb-4">This section is for future payment integration.</p>
          <div className="mt-6 flex justify-between">
            <button onClick={handleBack} className="text-blue-600">Back</button>
            <button onClick={handleSubmit} className="px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700">
              Confirm Booking
            </button>
          </div>
        </div>
      )}

      {step === 4 && submitted && (
        <div className="text-center p-10 bg-green-50 border rounded shadow mt-4">
          <h2 className="text-3xl text-green-700 font-bold mb-2">🎉 Appointment Booked!</h2>
          <p className="text-gray-700 mb-4">
            Thanks for booking with <span className="font-semibold text-blue-600">{selectedType?.name}</span>.
            A confirmation email has been sent.
          </p>
          <button
            onClick={() => {
              setStep(0);
              setSelectedType(null);
              setFormData({});
              setDate('');
              setSelectedSlot(null);
              setSubmitted(false);
            }}
            className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Book Another Appointment
          </button>
        </div>
      )}
    </div>
  );
};

export default Booking;
