import React, { useState, useEffect } from 'react';
import TimeSlotSelector from '../components/TimeSlotSelector';
import BookingForm from '../components/BookingForm';
import { fetchAvailableSlots } from '../utils/api';

const businessTypes = [
  { id: 'doctor', name: 'Doctor / Clinic' },
  { id: 'salon', name: 'Salon / Spa' },
  { id: 'coach', name: 'Coach / Trainer' },
  { id: 'consultant', name: 'Consultant' },
];

const Booking = () => {
  const [step, setStep] = useState(1);
  const [selectedType, setSelectedType] = useState(null);
  const [formData, setFormData] = useState({});
  const [date, setDate] = useState('');
  const [slots, setSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    if (date && selectedType) {
      fetchAvailableSlots(date).then(setSlots).catch(console.error);
    }
  }, [date, selectedType]);

  const handleSubmit = async () => {
    const payload = {
      ...formData,
      businessType: selectedType?.id,
      date,
      time: selectedSlot?.time,
    };

    try {
      const res = await fetch('http://localhost:5000/api/bookings', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const result = await res.json();
      if (res.ok) {
        setStep(5);
        setSubmitted(true);
      } else {
        alert(result.message || 'Failed to book appointment');
      }
    } catch (err) {
      alert('Something went wrong. Please try again.');
    }
  };

  const steps = ['Service', 'Time', 'Details', 'Payment', 'Done'];

  return (
    <div className="max-w-3xl mx-auto p-8">
      <h2 className="text-3xl font-bold mb-6 text-center">Book an Appointment</h2>

      {/* Progress Bar */}
      <div className="flex items-center mb-8">
        {steps.map((label, i) => (
          <div key={i} className="flex items-center w-full">
            <div className={`flex items-center justify-center w-8 h-8 rounded-full text-white text-sm font-bold ${
              step === i + 1 ? 'bg-blue-600' : i + 1 < step ? 'bg-green-500' : 'bg-gray-300'
            }`}>
              {i + 1}
            </div>
            <span className="ml-2 text-sm">{label}</span>
            {i < steps.length - 1 && <div className="flex-grow h-1 bg-gray-300 mx-2"></div>}
          </div>
        ))}
      </div>

      {/* Step 1: Service Selection */}
      {step === 1 && (
        <>
          <h3 className="mb-4 text-lg font-medium">Select a Service:</h3>
          <div className="grid grid-cols-2 gap-4 mb-6">
            {businessTypes.map((type) => (
              <button
                key={type.id}
                onClick={() => {
                  setSelectedType(type);
                  setStep(2);
                }}
                className={`border rounded p-4 ${
                  selectedType?.id === type.id ? 'bg-blue-100 border-blue-600' : 'hover:bg-blue-50'
                }`}
              >
                {type.name}
              </button>
            ))}
          </div>
        </>
      )}

      {/* Step 2: Time Selection */}
      {step === 2 && (
        <>
          <label className="block mb-2">Select a Date:</label>
          <input
            type="date"
            className="border p-2 w-full rounded mb-4"
            value={date}
            onChange={(e) => {
              setDate(e.target.value);
              setSelectedSlot(null);
            }}
          />
          {slots.length > 0 && (
            <>
              <label className="block mb-2">Select a Time Slot:</label>
              <TimeSlotSelector slots={slots} onSelect={setSelectedSlot} />
            </>
          )}
          <div className="mt-6 flex justify-between">
            <button onClick={() => setStep(1)} className="px-4 py-2 bg-gray-200 rounded">Back</button>
            <button
              onClick={() => selectedSlot && setStep(3)}
              className={`px-4 py-2 bg-blue-600 text-white rounded ${
                !selectedSlot ? 'opacity-50 cursor-not-allowed' : ''
              }`}
              disabled={!selectedSlot}
            >
              Next
            </button>
          </div>
        </>
      )}

      {/* Step 3: Customer Details Form */}
      {step === 3 && (
        <>
          <h3 className="mb-4 text-lg font-medium">Your Details:</h3>
          <BookingForm formData={formData} setFormData={setFormData} />
          <div className="mt-6 flex justify-between">
            <button onClick={() => setStep(2)} className="px-4 py-2 bg-gray-200 rounded">Back</button>
            <button
              onClick={() => formData.name && setStep(4)}
              className={`px-4 py-2 bg-blue-600 text-white rounded ${
                !formData.name ? 'opacity-50 cursor-not-allowed' : ''
              }`}
              disabled={!formData.name}
            >
              Next
            </button>
          </div>
        </>
      )}

      {/* Step 4: Payment Placeholder */}
      {step === 4 && (
        <>
          <h3 className="text-lg font-medium mb-4">💳 Payment</h3>
          <p className="text-gray-700 mb-6">Payment integration coming soon. For now, proceed to submit your booking.</p>
          <div className="flex justify-between">
            <button onClick={() => setStep(3)} className="px-4 py-2 bg-gray-200 rounded">Back</button>
            <button
              onClick={handleSubmit}
              className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
            >
              Confirm & Submit ✅
            </button>
          </div>
        </>
      )}

      {/* Step 5: Thank You */}
      {step === 5 && submitted && (
        <div className="text-center p-6 border rounded bg-green-50">
          <h2 className="text-2xl font-bold text-green-700 mb-2">✅ Appointment Confirmed</h2>
          <p className="text-gray-700 mb-4">
            Thank you for booking with <span className="font-semibold">{selectedType?.name}</span>. A confirmation email has been sent.
          </p>
          <button
            onClick={() => {
              setStep(1);
              setFormData({});
              setDate('');
              setSelectedType(null);
              setSelectedSlot(null);
              setSubmitted(false);
            }}
            className="px-4 py-2 bg-blue-600 text-white rounded"
          >
            Book Another Appointment
          </button>
        </div>
      )}
    </div>
  );
};

export default Booking;
