import React, { useState, useEffect } from 'react';

const TABS = ['Services', 'Time Slots', 'Bookings', 'Reports'];

const AdminPanel = () => {
  const [activeTab, setActiveTab] = useState('Services');

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">üõ†Ô∏è Admin Dashboard</h1>

      {/* Tabs */}
      <div className="flex space-x-4 border-b pb-2 mb-6">
        {TABS.map((tab) => (
          <button
            key={tab}
            className={`px-4 py-2 font-medium rounded-t-md ${
              activeTab === tab
                ? 'bg-blue-600 text-white'
                : 'bg-gray-100 text-gray-700 hover:bg-blue-50'
            }`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Tab Content */}
      <div className="bg-white p-6 rounded shadow-md border">
        {activeTab === 'Services' && <ServicesSection />}
        {activeTab === 'Time Slots' && <SlotsSection />}
        {activeTab === 'Bookings' && <BookingsSection />}
        {activeTab === 'Reports' && <ReportsSection />}
      </div>
    </div>
  );
};

// ‚úÖ Services Section
const ServicesSection = () => {
  const [services, setServices] = useState([]);
  const [name, setName] = useState('');

  const addService = () => {
    if (!name.trim()) return;
    setServices([...services, { id: Date.now(), name }]);
    setName('');
  };

  const deleteService = (id) => {
    setServices(services.filter((s) => s.id !== id));
  };

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">üìã Manage Services</h2>
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="New service name"
          className="border p-2 rounded w-full"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <button
          onClick={addService}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Add
        </button>
      </div>
      <ul>
        {services.map((service) => (
          <li
            key={service.id}
            className="flex justify-between py-2 border-b"
          >
            {service.name}
            <button
              onClick={() => deleteService(service.id)}
              className="text-red-600"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

// ‚úÖ Time Slots Section
const SlotsSection = () => {
  const [slots, setSlots] = useState([]);
  const [time, setTime] = useState('');

  const addSlot = () => {
    if (!time.trim()) return;
    setSlots([...slots, { id: Date.now(), time }]);
    setTime('');
  };

  const deleteSlot = (id) => {
    setSlots(slots.filter((s) => s.id !== id));
  };

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">üïí Manage Time Slots</h2>
      <div className="flex gap-2 mb-4">
        <input
          type="time"
          className="border p-2 rounded"
          value={time}
          onChange={(e) => setTime(e.target.value)}
        />
        <button
          onClick={addSlot}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Add
        </button>
      </div>
      <ul>
        {slots.map((slot) => (
          <li key={slot.id} className="flex justify-between py-2 border-b">
            {slot.time}
            <button
              onClick={() => deleteSlot(slot.id)}
              className="text-red-600"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

// ‚úÖ Bookings Section
const BookingsSection = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/bookings')
      .then((res) => res.json())
      .then(setBookings)
      .catch(console.error);
  }, []);

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">üìÖ All Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings yet.</p>
      ) : (
        <table className="w-full border mt-4 text-sm">
          <thead>
            <tr className="bg-gray-100">
              <th className="p-2 border">Name</th>
              <th className="p-2 border">Email</th>
              <th className="p-2 border">Phone</th>
              <th className="p-2 border">Service</th>
              <th className="p-2 border">Date</th>
              <th className="p-2 border">Time</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((b, i) => (
              <tr key={i}>
                <td className="p-2 border">{b.name}</td>
                <td className="p-2 border">{b.email}</td>
                <td className="p-2 border">{b.phone}</td>
                <td className="p-2 border">{b.businessType}</td>
                <td className="p-2 border">{b.date}</td>
                <td className="p-2 border">{b.time}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

// ‚úÖ Reports Section
const ReportsSection = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/bookings')
      .then((res) => res.json())
      .then(setBookings)
      .catch(console.error);
  }, []);

  const getTodayDate = () => new Date().toISOString().split('T')[0];

  const countByDate = bookings.reduce((acc, booking) => {
    acc[booking.date] = (acc[booking.date] || 0) + 1;
    return acc;
  }, {});

  const sortedDates = Object.keys(countByDate).sort((a, b) => new Date(b) - new Date(a));

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">üìà Booking Reports</h2>
      {bookings.length === 0 ? (
        <p>No bookings available for report.</p>
      ) : (
        <table className="w-full mt-4 text-sm border">
          <thead>
            <tr className="bg-gray-100">
              <th className="p-2 border text-left">Date</th>
              <th className="p-2 border text-left">Total Appointments</th>
            </tr>
          </thead>
          <tbody>
            {sortedDates.map((date) => (
              <tr key={date}>
                <td className="p-2 border">{date}</td>
                <td className="p-2 border">{countByDate[date]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AdminPanel;
