import React, { useState } from 'react';

const Booking = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    service: '',
    date: '',
    time: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleBooking = () => {
    // You will connect this to backend API
    console.log("Booking data submitted:", formData);
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 bg-white shadow-lg rounded-2xl">
      <h2 className="text-2xl font-bold mb-4 text-center">Book an Appointment</h2>
      <div className="space-y-4">
        <input
          name="name"
          type="text"
          placeholder="Your Name"
          onChange={handleChange}
          className="w-full px-4 py-2 border rounded"
        />
        <input
          name="email"
          type="email"
          placeholder="Email Address"
          onChange={handleChange}
          className="w-full px-4 py-2 border rounded"
        />
        <select
          name="service"
          onChange={handleChange}
          className="w-full px-4 py-2 border rounded"
        >
          <option value="">Select Service</option>
          <option value="consultation">Consultation</option>
          <option value="therapy">Therapy</option>
        </select>
        <input
          name="date"
          type="date"
          onChange={handleChange}
          className="w-full px-4 py-2 border rounded"
        />
        <input
          name="time"
          type="time"
          onChange={handleChange}
          className="w-full px-4 py-2 border rounded"
        />
        <button
          onClick={handleBooking}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-xl transition"
        >
          Book Appointment
        </button>
      </div>
    </div>
  );
};

export default Booking;
