import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Footer from '../components/Footer';

const Dashboard = () => {
  const [bookings, setBookings] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    const fetchBookings = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/bookings/my', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!res.ok) {
          if (res.status === 401) navigate('/login'); // Unauthorized
          throw new Error('Failed to fetch bookings');
        }

        const data = await res.json();
        setBookings(data);
      } catch (err) {
        console.error('Error fetching bookings:', err.message);
      }
    };

    fetchBookings();
  }, [navigate]);

  const handleCancel = async (id) => {
    const confirm = window.confirm('Are you sure you want to cancel this appointment?');
    if (!confirm) return;

    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/bookings/${id}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (res.ok) {
        setBookings((prev) => prev.filter((b) => b.id !== id));
        alert('Appointment cancelled.');
      } else {
        alert('Failed to cancel appointment.');
      }
    } catch (err) {
      console.error(err);
      alert('Server error while cancelling.');
    }
  };

  return (
    <>
      <div className="min-h-screen p-8 max-w-4xl mx-auto">
        <h2 className="text-3xl font-bold mb-6 text-center">ðŸ“‹ My Appointments</h2>

        {bookings.length === 0 ? (
          <p className="text-center text-gray-600">You haven't booked any appointments yet.</p>
        ) : (
          <div className="grid gap-4">
            {bookings.map((booking) => (
              <div
                key={booking.id}
                className="border rounded-lg p-4 shadow-sm bg-white hover:shadow-md transition"
              >
                <div className="flex justify-between items-center">
                  <div>
                    <h3 className="text-xl font-semibold text-blue-700">{booking.businessType}</h3>
                    <p className="text-gray-700">
                      ðŸ‘¤ {booking.name} <br />
                      ðŸ“… {booking.date} at ðŸ•’ {booking.time}
                    </p>
                    <p className="mt-1 text-sm text-gray-500">
                      Status:{' '}
                      <span
                        className={`font-semibold ${
                          booking.status === 'Confirmed'
                            ? 'text-green-600'
                            : 'text-yellow-600'
                        }`}
                      >
                        {booking.status}
                      </span>
                    </p>
                  </div>
                  <button
                    className="bg-red-500 text-white px-4 py-1 rounded hover:bg-red-600 transition text-sm"
                    onClick={() => handleCancel(booking.id)}
                  >
                    Cancel
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default Dashboard;
