import React, { useState, useEffect } from 'react';
import TimeSlotSelector from '../components/TimeSlotSelector';
import BookingForm from '../components/BookingForm';
import { fetchAvailableSlots } from '../utils/api';

const businessTypes = [
  { id: 'doctor', name: 'Doctor / Clinic', description: 'Book medical consultations or checkups.' },
  { id: 'salon', name: 'Salon / Spa', description: 'Schedule your beauty or grooming appointment.' },
  { id: 'coach', name: 'Coach / Trainer', description: 'Book a personal training or mentoring session.' },
  { id: 'consultant', name: 'Consultant', description: 'Reserve time for business or legal advice.' }
];

const Booking = () => {
  const [selectedType, setSelectedType] = useState(null);
  const [date, setDate] = useState('');
  const [slots, setSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null);

  useEffect(() => {
    if (date && selectedType) {
      fetchAvailableSlots(date).then(setSlots).catch(console.error);
    }
  }, [date, selectedType]);

  const handleSelect = (type) => {
    setSelectedType(type);
    setSelectedSlot(null);
    setDate('');
    setTimeout(() => {
      const el = document.getElementById('date-select');
      if (el) el.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  return (
    <div className="p-8 max-w-3xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">Book an Appointment</h2>

      {/* Step 1: Select business type */}
      <div className="mb-8">
        <h3 className="text-lg font-medium mb-2">1️⃣ Select the type of appointment:</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {businessTypes.map((type) => (
            <button
              key={type.id}
              onClick={() => handleSelect(type)}
              className={`border rounded-lg p-4 hover:bg-blue-50 transition ${
                selectedType?.id === type.id ? 'border-blue-600 bg-blue-100' : 'border-gray-300'
              }`}
            >
              <h4 className="font-semibold text-sm">{type.name}</h4>
            </button>
          ))}
        </div>
        {selectedType && (
          <p className="mt-4 text-blue-700">{selectedType.description}</p>
        )}
      </div>

      {/* Step 2: Select date */}
      {selectedType && (
        <div id="date-select" className="mb-6">
          <label className="block mb-2 text-sm font-medium">2️⃣ Select a date:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => {
              setDate(e.target.value);
              setSelectedSlot(null);
            }}
            className="p-2 border rounded w-full"
          />
        </div>
      )}

      {/* Step 3: Select time slot */}
      {date && slots.length > 0 && (
        <div className="mb-6">
          <label className="block mb-2 text-sm font-medium">3️⃣ Select a time slot:</label>
          <TimeSlotSelector
            slots={slots}
            onSelect={(slot) => {
              setSelectedSlot(slot);
              setTimeout(() => {
                const el = document.getElementById('booking-form');
                if (el) el.scrollIntoView({ behavior: 'smooth' });
              }, 100);
            }}
          />
        </div>
      )}

      {/* Step 4: Show booking form */}
      {selectedSlot && (
        <div id="booking-form" className="mt-8">
          <h3 className="text-lg font-medium mb-4">
            4️⃣ Enter your details to confirm your {selectedType.name} appointment:
          </h3>
          <BookingForm
            businessType={selectedType.id}
            date={date}
            time={selectedSlot.time}
          />
        </div>
      )}
    </div>
  );
};

export default Booking;
