import React, { useState, useEffect } from 'react';
import TimeSlotSelector from '../components/TimeSlotSelector';
import BookingForm from '../components/BookingForm';
import { fetchAvailableSlots } from '../utils/api';

const steps = ['Service', 'Time', 'Details', 'Payment', 'Done'];

const Booking = () => {
  const [step, setStep] = useState(0);
  const [selectedType, setSelectedType] = useState(null);
  const [formData, setFormData] = useState({});
  const [date, setDate] = useState('');
  const [slots, setSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null);

  useEffect(() => {
    if (date && selectedType) {
      fetchAvailableSlots(date).then(setSlots).catch(console.error);
    }
  }, [date, selectedType]);

  const handleNext = () => {
    if (step < steps.length - 1) setStep(step + 1);
  };

  const handlePrev = () => {
    if (step > 0) setStep(step - 1);
  };

  const handleServiceSelect = (type) => {
    setSelectedType(type);
    handleNext();
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-3xl font-bold text-center mb-6">Book an Appointment</h2>

      {/* Step Progress Bar */}
      <div className="flex justify-between items-center mb-10">
        {steps.map((label, index) => (
          <div key={index} className="flex-1 flex flex-col items-center">
            <div
              className={`w-8 h-8 flex items-center justify-center rounded-full text-sm font-bold ${
                index <= step ? 'bg-blue-600 text-white' : 'bg-gray-300 text-gray-600'
              }`}
            >
              {index + 1}
            </div>
            <span className="text-xs mt-2">{label}</span>
          </div>
        ))}
      </div>

      {/* Step Screens */}
      {step === 0 && (
        <div>
          <h3 className="mb-4 text-lg font-semibold">Select the type of service</h3>
          <div className="grid grid-cols-2 gap-4">
            {['Doctor', 'Salon', 'Coach', 'Consultant'].map((type) => (
              <button
                key={type}
                onClick={() => handleServiceSelect({ id: type.toLowerCase(), name: type })}
                className={`p-4 border rounded hover:bg-blue-50 ${
                  selectedType?.name === type ? 'border-blue-600 bg-blue-100' : ''
                }`}
              >
                {type}
              </button>
            ))}
          </div>
        </div>
      )}

      {step === 1 && (
        <div>
          <label className="block text-sm font-medium mb-2 mt-4">Select a Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => {
              setDate(e.target.value);
              setSelectedSlot(null);
            }}
            className="border p-2 rounded w-full mb-4"
          />
          {slots.length > 0 && (
            <>
              <label className="block text-sm font-medium mb-2">Available Time Slots</label>
              <TimeSlotSelector slots={slots} onSelect={setSelectedSlot} />
            </>
          )}
        </div>
      )}

      {step === 2 && (
        <div>
          <h3 className="mb-4 text-lg font-semibold">Enter Your Details</h3>
          <BookingForm formData={formData} setFormData={setFormData} />
        </div>
      )}

      {/* Navigation Buttons */}
      <div className="flex justify-between mt-10">
        {step > 0 && (
          <button onClick={handlePrev} className="px-4 py-2 bg-gray-300 rounded">
            ⬅ Back
          </button>
        )}
        {step < steps.length - 1 && (
          <button
            onClick={handleNext}
            className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Next ➡
          </button>
        )}
      </div>
    </div>
  );
};

export default Booking;
