import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center p-8">
      <div className="max-w-4xl mx-auto grid md:grid-cols-2 gap-10 items-center">
        {/* Image */}
        <img
          src="/public/images/download.png"
          alt="Booking Illustration"
          className="w-full h-auto"
        />

        {/* Text Section */}
        <div>
          <h1 className="text-4xl font-bold mb-4 text-blue-700">Welcome to the Booking System</h1>
          <p className="text-lg text-gray-700 mb-6">
            Book your appointments effortlessly. Get instant confirmations and reminders‚Äîit's simple, fast, and reliable!
          </p>

          <ul className="mb-6 text-left list-disc pl-6 text-gray-600">
            <li>üìÖ Real-time availability</li>
            <li>üîî Email reminders</li>
            <li>üîê Secure user accounts</li>
            <li>üí≥ Optional online payments</li>
          </ul>

          <Link
            to="/booking"
            className="inline-block bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition"
          >
            Book an Appointment
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Home;
