import React, { useState, useEffect } from 'react';
import TimeSlotSelector from '../components/TimeSlotSelector';
import BookingForm from '../components/BookingForm';
import { fetchAvailableSlots } from '../utils/api';

const businessTypes = [
  { id: 'doctor', name: 'Doctor / Clinic', description: 'Book medical consultations or checkups.' },
  { id: 'salon', name: 'Salon / Spa', description: 'Schedule your beauty or grooming appointment.' },
  { id: 'coach', name: 'Coach / Trainer', description: 'Book a personal training or mentoring session.' },
  { id: 'consultant', name: 'Consultant', description: 'Reserve time for business or legal advice.' }
];

const Booking = () => {
  const [selectedType, setSelectedType] = useState(null);
  const [formData, setFormData] = useState({});
  const [date, setDate] = useState('');
  const [slots, setSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [submitted, setSubmitted] = useState(false);

  // Load slots when date or type changes
  useEffect(() => {
    if (date && selectedType) {
      fetchAvailableSlots(date).then(setSlots).catch(console.error);
    }
  }, [date, selectedType]);

  const resetAll = () => {
    setSelectedType(null);
    setFormData({});
    setDate('');
    setSelectedSlot(null);
    setSubmitted(false);
  };

  const handleSelectType = (type) => {
    resetAll();
    setSelectedType(type);

    // Scroll to form
    setTimeout(() => {
      const formEl = document.getElementById('customer-form');
      if (formEl) formEl.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  const handleSubmit = async () => {
    const payload = {
      ...formData,
      businessType: selectedType.id,
      date,
      time: selectedSlot?.time || 'Not selected',
    };

    try {
      const res = await fetch('http://localhost:5000/api/bookings', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const result = await res.json();
      if (res.ok) {
        setSubmitted(true);
      } else {
        alert(result.message || 'Failed to book appointment');
      }
    } catch (err) {
      alert('Something went wrong. Please try again.');
    }
  };

  // ‚úÖ Thank You Screen
  if (submitted) {
    return (
      <div className="p-10 text-center max-w-xl mx-auto bg-green-50 border border-green-200 rounded-lg shadow">
        <h2 className="text-3xl font-bold text-green-700 mb-4">‚úÖ Appointment Confirmed!</h2>
        <p className="text-lg text-gray-700 mb-2">
          Thank you for booking with <span className="font-semibold text-blue-600">{selectedType?.name}</span>.
        </p>
        <p className="text-md text-gray-600 mb-6">
          A confirmation email will be sent to you shortly with the details.
        </p>
        <button
          onClick={resetAll}
          className="px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
        >
          üìÖ Book Another Appointment
        </button>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-3xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">Book an Appointment</h2>

      {/* Step 1: Select Business Type */}
      <div className="mb-8">
        <h3 className="text-lg font-medium mb-2">1Ô∏è‚É£ Choose a service type:</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {businessTypes.map((type) => (
            <button
              key={type.id}
              onClick={() => handleSelectType(type)}
              className={`border rounded-lg p-4 hover:bg-blue-50 transition ${
                selectedType?.id === type.id ? 'border-blue-600 bg-blue-100' : 'border-gray-300'
              }`}
            >
              <h4 className="font-semibold text-sm">{type.name}</h4>
            </button>
          ))}
        </div>
      </div>

      {/* Step 2: Customer Info Form */}
      {selectedType && (
        <div id="customer-form" className="mb-6">
          <h3 className="text-lg font-medium mb-4">2Ô∏è‚É£ Enter your details:</h3>
          <BookingForm
            businessType={selectedType.id}
            formData={formData}
            setFormData={setFormData}
          />
        </div>
      )}

      {/* Step 3: Select Date */}
      {selectedType && (
        <div className="mb-6">
          <h3 className="text-sm font-medium mb-2">3Ô∏è‚É£ Choose a Date:</h3>
          <input
            type="date"
            value={date}
            onChange={(e) => {
              setDate(e.target.value);
              setSelectedSlot(null);
            }}
            className="p-2 border rounded w-full"
          />
        </div>
      )}

      {/* Step 4: Time Slots */}
      {date && slots.length > 0 && (
        <div className="mb-6">
          <h3 className="text-sm font-medium mb-2">4Ô∏è‚É£ Choose a Time Slot:</h3>
          <TimeSlotSelector
            slots={slots}
            onSelect={setSelectedSlot}
          />
        </div>
      )}

      {/* Step 5: Submit */}
      {selectedType && formData.name && date && (
        <div className="mt-6">
          <button
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
            onClick={handleSubmit}
          >
            ‚úÖ Submit Booking
          </button>
        </div>
      )}
    </div>
  );
};

export default Booking;
