import React, { useState, useEffect } from 'react';

const mockBookings = [
  {
    id: 1,
    name: 'Dhiraj Wanjari',
    service: 'Doctor / Clinic',
    date: '2025-07-05',
    time: '10:30 AM',
    status: 'Confirmed',
  },
  {
    id: 2,
    name: 'Dhiraj Wanjari',
    service: 'Salon / Spa',
    date: '2025-07-10',
    time: '04:00 PM',
    status: 'Pending',
  },
];

const AdminPanel = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    // Replace with fetch from backend in real app
    setBookings(mockBookings);
  }, []);

  return (
    <div className="p-8 max-w-5xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">🛠️ Admin Panel</h2>

      {/* Section: Booking List */}
      <section className="mb-10">
        <h3 className="text-xl font-semibold mb-4">📋 All Bookings</h3>
        {bookings.length === 0 ? (
          <p>No bookings found.</p>
        ) : (
          <div className="grid gap-4">
            {bookings.map((b) => (
              <div key={b.id} className="border rounded p-4 bg-white shadow-sm">
                <div className="flex justify-between">
                  <div>
                    <p className="text-lg font-medium text-blue-600">{b.service}</p>
                    <p>
                      👤 {b.name} | 📅 {b.date} at {b.time}
                    </p>
                    <p className="text-sm text-gray-500">
                      Status: <span className={b.status === 'Confirmed' ? 'text-green-600' : 'text-yellow-600'}>{b.status}</span>
                    </p>
                  </div>
                  <button
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 text-sm h-fit"
                    onClick={() => alert('Cancel/Delete booking logic goes here')}
                  >
                    ❌ Remove
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Section: Manage Time Slots */}
      <section className="mb-10">
        <h3 className="text-xl font-semibold mb-4">📅 Manage Time Slots</h3>
        <button
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          onClick={() => alert('Add time slot logic here')}
        >
          ➕ Add Time Slot
        </button>
        {/* You could display time slot list and allow delete/edit here */}
      </section>

      {/* Section: Services */}
      <section>
        <h3 className="text-xl font-semibold mb-4">💼 Manage Services</h3>
        <button
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 mr-2"
          onClick={() => alert('Add service logic here')}
        >
          ➕ Add Service
        </button>
        <button
          className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700"
          onClick={() => alert('Edit/delete service logic here')}
        >
          ⚙️ Edit / Delete Services
        </button>
      </section>
    </div>
  );
};

export default AdminPanel;
