import React, { useState } from 'react';

const AdminPanel = () => {
  const [activeTab, setActiveTab] = useState('appointments');

  const renderContent = () => {
    switch (activeTab) {
      case 'appointments':
        return (
          <div>
            <h3 className="text-xl font-bold mb-3">All Appointments</h3>
            <p>Here you can view or cancel bookings.</p>
            {/* Add table of bookings here */}
          </div>
        );
      case 'slots':
        return (
          <div>
            <h3 className="text-xl font-bold mb-3">Time Slots</h3>
            <p>Add or remove available time slots for booking.</p>
            {/* Add form or list of slots */}
          </div>
        );
      case 'services':
        return (
          <div>
            <h3 className="text-xl font-bold mb-3">Services</h3>
            <p>Manage available services like Doctor, Salon, etc.</p>
            {/* Add service list / CRUD */}
          </div>
        );
      case 'settings':
        return (
          <div>
            <h3 className="text-xl font-bold mb-3">Settings</h3>
            <p>Update admin preferences, email notifications, etc.</p>
            {/* Add settings controls */}
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="p-8 max-w-5xl mx-auto">
      <h2 className="text-3xl font-semibold mb-6 text-center">üîß Admin Dashboard</h2>

      <div className="flex space-x-4 mb-8">
        <button
          className={`px-4 py-2 rounded ${
            activeTab === 'appointments'
              ? 'bg-blue-600 text-white'
              : 'bg-gray-100 hover:bg-blue-100'
          }`}
          onClick={() => setActiveTab('appointments')}
        >
          üìÖ Appointments
        </button>
        <button
          className={`px-4 py-2 rounded ${
            activeTab === 'slots'
              ? 'bg-blue-600 text-white'
              : 'bg-gray-100 hover:bg-blue-100'
          }`}
          onClick={() => setActiveTab('slots')}
        >
          üïí Time Slots
        </button>
        <button
          className={`px-4 py-2 rounded ${
            activeTab === 'services'
              ? 'bg-blue-600 text-white'
              : 'bg-gray-100 hover:bg-blue-100'
          }`}
          onClick={() => setActiveTab('services')}
        >
          üßë‚Äçüíº Services
        </button>
        <button
          className={`px-4 py-2 rounded ${
            activeTab === 'settings'
              ? 'bg-blue-600 text-white'
              : 'bg-gray-100 hover:bg-blue-100'
          }`}
          onClick={() => setActiveTab('settings')}
        >
          ‚öôÔ∏è Settings
        </button>
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md border">{renderContent()}</div>
    </div>
  );
};

export default AdminPanel;
