import React, { useState, useEffect } from 'react';
import BookingForm from '../components/BookingForm';

const businessTypes = [
  { id: 'doctor', name: 'Doctor / Clinic', image: '/images/doctor.png' },
  { id: 'salon', name: 'Salon / Spa', image: '/images/salon.png' },
  { id: 'coach', name: 'Coach / Trainer', image: '/images/coach.png' },
  { id: 'consultant', name: 'Consultant', image: '/images/consultant.png' },
  { id: 'dentist', name: 'Dentist', image: '/images/dentist.png' },
  { id: 'therapist', name: 'Therapist', image: '/images/therapist.png' },
  { id: 'photographer', name: 'Photographer', image: '/images/photographer.png' },
  { id: 'mechanic', name: 'Mechanic', image: '/images/mechanic.png' },
];

const Booking = () => {
  const [step, setStep] = useState(1);
  const [selectedType, setSelectedType] = useState(null);
  const [formData, setFormData] = useState({});
  const [date, setDate] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [availableSlots, setAvailableSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState('');

  const isFormValid = formData.name && formData.email && formData.phone && selectedSlot;

  useEffect(() => {
    const fetchSlots = async () => {
      if (!date || !selectedType) return;

      try {
        const res = await fetch(`http://localhost:5000/api/slots?date=${date}&serviceId=${selectedType.id}`);
        const data = await res.json();
        setAvailableSlots(data.slots || []);
      } catch (err) {
        console.error('Error fetching slots:', err);
      }
    };

    fetchSlots();
  }, [date, selectedType]);

  const handleSubmit = async () => {
    const payload = {
      ...formData,
      businessType: selectedType?.id,
      date,
      time: selectedSlot,
    };

    try {
      const res = await fetch('http://localhost:5000/api/bookings', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const result = await res.json();
      if (res.ok) {
        setStep(4);
        setSubmitted(true);
      } else {
        alert(result.message || 'Failed to book appointment');
      }
    } catch (err) {
      alert('Something went wrong. Please try again.');
    }
  };

  const steps = ['Service', 'Date', 'Details', 'Done'];

  return (
    <div className="max-w-3xl mx-auto p-8">
      <h2 className="text-3xl font-bold mb-6 text-center">Book an Appointment</h2>

      {/* Progress Bar */}
      <div className="flex items-center mb-8">
        {steps.map((label, i) => (
          <div key={i} className="flex items-center w-full">
            <div
              className={`flex items-center justify-center w-8 h-8 rounded-full text-white text-sm font-bold ${
                step === i + 1 ? 'bg-blue-600' : i + 1 < step ? 'bg-green-500' : 'bg-gray-300'
              }`}
            >
              {i + 1}
            </div>
            <span className="ml-2 text-sm">{label}</span>
            {i < steps.length - 1 && <div className="flex-grow h-1 bg-gray-300 mx-2"></div>}
          </div>
        ))}
      </div>

      {/* Step 1: Service Selection */}
      {step === 1 && (
        <>
          <h3 className="mb-4 text-lg font-medium text-center">Select a Service</h3>
          <div className="grid grid-cols-2 sm:grid-cols-3 gap-6 mb-6">
            {[...businessTypes, ...customServices].map((type) => (

              <button
                key={type.id || type.name}
                onClick={() => {
                  setSelectedType(type);
                  setStep(2);
                }}
                className={`border rounded-lg p-4 flex flex-col items-center hover:shadow-lg transition ${
                  selectedType?.id === type.id || selectedType?.name === type.name ? 'bg-blue-100 border-blue-600' : 'hover:bg-blue-50'
                }`}
              >
                {type.image && <img src={type.image} alt={type.name} className="w-16 h-16 object-contain mb-2" />}
                <span className="text-lg font-medium">{type.name}</span>
              </button>

            ))}
          </div>
        </>
      )}

      {/* Step 2: Date + Slot Selection */}
      {step === 2 && (
        <>
          <label className="block mb-2 font-medium">Select a Date:</label>
          <input
            type="date"
            className="border p-2 w-full rounded mb-4"
            value={date}
            onChange={(e) => {
              setDate(e.target.value);
              setSelectedSlot('');
            }}
          />

          {date && (
            <>
              <label className="block mb-2 font-medium">Available Time Slots:</label>
              <div className="grid grid-cols-2 sm:grid-cols-3 gap-3 mb-4">
                {availableSlots.length > 0 ? (
                  availableSlots.map((slot, idx) => (
                    <button
                      key={idx}
                      onClick={() => setSelectedSlot(slot)}
                      className={`border px-3 py-2 rounded text-sm ${
                        selectedSlot === slot ? 'bg-blue-600 text-white' : 'hover:bg-blue-50'
                      }`}
                    >
                      {slot}
                    </button>
                  ))
                ) : (
                  <p className="text-gray-500 text-sm col-span-full">No available slots for this date.</p>
                )}
              </div>
            </>
          )}

          <div className="mt-6 flex justify-between">
            <button onClick={() => setStep(1)} className="px-4 py-2 bg-gray-200 rounded">
              Back
            </button>
            <button
              onClick={() => date && selectedSlot && setStep(3)}
              className={`px-4 py-2 bg-blue-600 text-white rounded ${
                !selectedSlot ? 'opacity-50 cursor-not-allowed' : ''
              }`}
              disabled={!selectedSlot}
            >
              Next
            </button>
          </div>
        </>
      )}

      {/* Step 3: Customer Details Form */}
      {step === 3 && (
        <>
          <h3 className="mb-4 text-lg font-medium">Your Details:</h3>
          <BookingForm formData={formData} setFormData={setFormData} />
          <div className="mt-6 flex justify-between">
            <button onClick={() => setStep(2)} className="px-4 py-2 bg-gray-200 rounded">
              Back
            </button>
            <button
              onClick={handleSubmit}
              className={`px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 ${
                !isFormValid ? 'opacity-50 cursor-not-allowed' : ''
              }`}
              disabled={!isFormValid}
            >
              Confirm & Submit ✅
            </button>
          </div>
        </>
      )}

      {/* Step 4: Thank You */}
      {step === 4 && submitted && (
        <div className="text-center p-6 border rounded bg-green-50">
          <h2 className="text-2xl font-bold text-green-700 mb-2">✅ Appointment Confirmed</h2>
          <p className="text-gray-700 mb-4">
            Thank you for booking with <span className="font-semibold">{selectedType?.name}</span>. A confirmation email has been sent.
          </p>
          <button
            onClick={() => {
              setStep(1);
              setFormData({});
              setDate('');
              setSelectedType(null);
              setSubmitted(false);
              setSelectedSlot('');
            }}
            className="px-4 py-2 bg-blue-600 text-white rounded"
          >
            Book Another Appointment
          </button>
        </div>
      )}
    </div>
  );
};

export default Booking;
