import React, { useState, useEffect } from 'react';
import { fetchServices, fetchAvailableSlots, bookAppointment } from '../utils/api';
import TimeSlotSelector from './TimeSlotSelector';

const BookingForm = () => {
  const [services, setServices] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', serviceId: '', date: '', time: '' });
  const [slots, setSlots] = useState([]);

  useEffect(() => {
    fetchServices().then(setServices);
  }, []);

  useEffect(() => {
    if (form.date && form.serviceId) {
      fetchAvailableSlots(form.date, form.serviceId).then(setSlots);
    }
  }, [form.date, form.serviceId]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSlotSelect = (slot) => {
    setForm({ ...form, time: slot.time });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await bookAppointment(form);
    alert(res.message || "Booking submitted");
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <input type="text" name="name" placeholder="Name" onChange={handleChange} className="w-full p-2 border" />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} className="w-full p-2 border" />
      <select name="serviceId" onChange={handleChange} className="w-full p-2 border">
        <option value="">Select Service</option>
        {services.map((s) => (
          <option key={s.id} value={s.id}>{s.name}</option>
        ))}
      </select>
      <input type="date" name="date" onChange={handleChange} className="w-full p-2 border" />
      <TimeSlotSelector slots={slots} onSelect={handleSlotSelect} />
      <button type="submit" className="w-full bg-blue-600 text-white p-2 rounded">Book Now</button>
    </form>
  );
};

export default BookingForm;
