import React, { useEffect, useState } from 'react';

// âœ… Reports Section
const ReportsT = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/bookings')
      .then((res) => res.json())
      .then(setBookings)
      .catch(console.error);
  }, []);

  const countByDate = bookings.reduce((acc, booking) => {
    acc[booking.date] = (acc[booking.date] || 0) + 1;
    return acc;
  }, {});

  const sortedDates = Object.keys(countByDate).sort((a, b) => new Date(b) - new Date(a));

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">ðŸ“ˆ Booking Reports</h2>
      {bookings.length === 0 ? (
        <p>No bookings available for report.</p>
      ) : (
        <table className="w-full mt-4 text-sm border">
          <thead>
            <tr className="bg-gray-100">
              <th className="p-2 border text-left">Date</th>
              <th className="p-2 border text-left">Total Appointments</th>
            </tr>
          </thead>
          <tbody>
            {sortedDates.map((date) => (
              <tr key={date}>
                <td className="p-2 border">{date}</td>
                <td className="p-2 border">{countByDate[date]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ReportTab;
