import React, { useState, useEffect } from 'react';

export default function SlotsTab() {
  const [serviceId, setServiceId] = useState('doctor');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [slots, setSlots] = useState([]);

  const token = localStorage.getItem('token');

  const fetchSlots = async () => {
    if (!serviceId || !date) return;
    const res = await fetch(
      `http://localhost:5000/api/slots?serviceId=${serviceId}&date=${date}`
    );
    const data = await res.json();
    setSlots(data);
  };

  const handleAdd = async () => {
    if (!serviceId || !date || !time) return;

    const res = await fetch('http://localhost:5000/api/slots', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ serviceId, date, time }),
    });

    if (res.ok) {
      setTime('');
      fetchSlots();
    } else alert('Error adding slot');
  };

  const handleDelete = async (id) => {
    const res = await fetch(`http://localhost:5000/api/slots/${id}`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` },
    });
    if (res.ok) fetchSlots();
    else alert('Delete failed');
  };

  useEffect(() => {
    fetchSlots();
  }, [serviceId, date]);

  return (
    <div>
      <h3 className="text-xl font-semibold mb-4">Manage Time Slots</h3>

      <div className="flex gap-2 mb-4">
        <select
          value={serviceId}
          onChange={(e) => setServiceId(e.target.value)}
          className="border p-2 rounded"
        >
          <option value="doctor">Doctor / Clinic</option>
          <option value="salon">Salon / Spa</option>
          {/* add more */}
        </select>

        <input
          type="date"
          className="border p-2 rounded"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />

        <input
          type="time"
          className="border p-2 rounded"
          value={time}
          onChange={(e) => setTime(e.target.value)}
        />
        <button onClick={handleAdd} className="bg-blue-600 text-white px-4 py-2 rounded">Add</button>
      </div>

      <ul>
        {slots.map((s) => (
          <li key={s.id} className="flex justify-between border-b py-2">
            {s.time}
            <button onClick={() => handleDelete(s.id)} className="text-red-600">Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
