import React, { useState } from 'react';

const steps = ['Service', 'Time', 'Details', 'Payment', 'Done'];

const BookingSteps = () => {
  const [step, setStep] = useState(0);
  const [form, setForm] = useState({
    category: '',
    service: '',
    employee: 'Any',
    date: '',
    daysAvailable: {
      Mon: true,
      Tue: true,
      Wed: true,
      Thu: true,
      Fri: true,
    },
    startTime: '08:00',
    endTime: '18:00',
  });

  const handleNext = () => {
    if (step < steps.length - 1) setStep(step + 1);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const toggleDay = (day) => {
    setForm({
      ...form,
      daysAvailable: {
        ...form.daysAvailable,
        [day]: !form.daysAvailable[day],
      },
    });
  };

  return (
    <div className="max-w-3xl mx-auto p-8 bg-white rounded shadow">
      <h2 className="text-2xl font-bold text-center mb-6">
        Bookly appointment widget for website â€“ Easy Way for Your Clients to Book
      </h2>

      {/* Progress Bar */}
      <div className="flex items-center justify-between mb-6">
        {steps.map((label, index) => (
          <div key={index} className="flex-1 text-center">
            <div
              className={`text-sm font-medium mb-1 ${
                step === index ? 'text-cyan-600' : 'text-gray-500'
              }`}
            >
              {index + 1}. {label}
            </div>
            <div
              className={`h-1 rounded-full ${
                step >= index ? 'bg-cyan-500' : 'bg-gray-300'
              }`}
            ></div>
          </div>
        ))}
      </div>

      {/* Step 1: Service Selection */}
      {step === 0 && (
        <div className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-semibold mb-1">Category</label>
              <select
                name="category"
                value={form.category}
                onChange={handleChange}
                className="border p-2 rounded w-full"
              >
                <option>Select category</option>
                <option>Doctor</option>
                <option>Salon</option>
                <option>Coach</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-semibold mb-1">Service</label>
              <select
                name="service"
                value={form.service}
                onChange={handleChange}
                className="border p-2 rounded w-full"
              >
                <option>Select service</option>
                <option>Consultation</option>
                <option>Haircut</option>
                <option>Fitness Session</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-semibold mb-1">Employee</label>
              <select
                name="employee"
                value={form.employee}
                onChange={handleChange}
                className="border p-2 rounded w-full"
              >
                <option>Any</option>
                <option>Employee A</option>
                <option>Employee B</option>
              </select>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 items-end">
            <div>
              <label className="block text-sm font-semibold mb-1">
                I'm available on or after
              </label>
              <input
                type="date"
                name="date"
                value={form.date}
                onChange={handleChange}
                className="border p-2 rounded w-full"
              />
            </div>
            <div className="col-span-2 flex space-x-3 items-center">
              {['Mon', 'Tue', 'Wed', 'Thu', 'Fri'].map((day) => (
                <div
                  key={day}
                  onClick={() => toggleDay(day)}
                  className={`cursor-pointer border rounded-full w-8 h-8 flex items-center justify-center text-sm font-semibold ${
                    form.daysAvailable[day]
                      ? 'bg-cyan-500 text-white'
                      : 'bg-gray-200 text-gray-600'
                  }`}
                >
                  {day[0]}
                </div>
              ))}
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-semibold mb-1">Start from</label>
              <input
                type="time"
                name="startTime"
                value={form.startTime}
                onChange={handleChange}
                className="border p-2 rounded w-full"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold mb-1">Finish by</label>
              <input
                type="time"
                name="endTime"
                value={form.endTime}
                onChange={handleChange}
                className="border p-2 rounded w-full"
              />
            </div>
          </div>

          <div className="text-right">
            <button
              onClick={handleNext}
              className="bg-cyan-500 text-white px-6 py-2 rounded hover:bg-cyan-600"
            >
              NEXT
            </button>
          </div>
        </div>
      )}

      {/* Add further steps here as per need (Time, Details, Payment, Done) */}
    </div>
  );
};

export default BookingSteps;
