import React, { useState, useEffect } from 'react';

const mockBookings = [
  {
    id: 1,
    name: 'Dhiraj Wanjari',
    businessType: 'Doctor / Clinic',
    date: '2025-07-05',
    time: '10:30 AM',
    status: 'Confirmed',
  },
  {
    id: 2,
    name: 'Dhiraj Wanjari',
    businessType: 'Salon / Spa',
    date: '2025-07-10',
    time: '04:00 PM',
    status: 'Pending',
  },
];

const Dashboard = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    // In a real app, you'd fetch user bookings from the backend
    setBookings(mockBookings);
  }, []);

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">ðŸ“‹ My Appointments</h2>

      {bookings.length === 0 ? (
        <p className="text-center text-gray-600">You haven't booked any appointments yet.</p>
      ) : (
        <div className="grid gap-4">
          {bookings.map((booking) => (
            <div
              key={booking.id}
              className="border rounded-lg p-4 shadow-sm bg-white hover:shadow-md transition"
            >
              <div className="flex justify-between items-center">
                <div>
                  <h3 className="text-xl font-semibold text-blue-700">{booking.businessType}</h3>
                  <p className="text-gray-700">
                    ðŸ‘¤ {booking.name} <br />
                    ðŸ“… {booking.date} at ðŸ•’ {booking.time}
                  </p>
                  <p className="mt-1 text-sm text-gray-500">Status: <span className={`font-semibold ${booking.status === 'Confirmed' ? 'text-green-600' : 'text-yellow-600'}`}>{booking.status}</span></p>
                </div>
                <button
                  className="bg-red-500 text-white px-4 py-1 rounded hover:bg-red-600 transition text-sm"
                  onClick={() => alert('Cancel logic goes here')}
                >
                  Cancel
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
